 intersect(gene_list,colnames(test_r))","  test_r <- log1p(as.matrix(test_r[,remain_genes])) / log(2)","  prob_r <- prob_r[remain_genes,]","  total <- as.matrix(test_r) %*% as.matrix(prob_r)","  cellType <- c()","  for(i in 1:nrow(total)){","    index <- which.max(total[i,])","    cellType <- c(cellType,colnames(total)[index])","  }","  return(cellType)","}","","#' @name celltype_predict","#' @param matrix Rows should be cells and columns should be genes.","#' @return  'cellType'","","celltype_predict <- function(matrix){","  l <- data(\"panC_cell_ref\",package=\"SPECTRUM\")","  panC_cell_ref <- eval(parse(text = l))","  prob_r <- t(panC_cell_ref)","  out <- Gambler_R(matrix, prob_r, gene_list = rownames(prob_r))","  return(out)","}","","#' @name patient_group_predict","#' @param proportion Rows should be samples and columns should be cell types.","#' @return  'group'","","patient_group_predict <- function(proportion){","  l <- data(\"panC_samp_ref\",package=\"SPECTRUM\")","  panC_samp_ref <- eval(parse(text = l))","  prob_r <- t(panC_samp_ref)","  proportion <- proportion * 1000000","  out <- Gambler_R(proportion, prob_r, gene_list = rownames(prob_r))","  return(out)","}","","#' @name celltype_predict","#' @param matrix Rows should be cells and columns should be genes.","#' @param sampleID should be a vector of samplID matched with each cell in matrix","#' @return  'group'","merge_predict <- function(matrix, sampleID = NULL){","  cell.type <- celltype_predict(matrix)","  df <- as.data.frame(cbind(cell.type,sampleID))","  colnames(df) <- c(\"cell.type\",\"sampleID\")","  proportion <- table(df$sampleID, df$cell.type)","  proportion <- as.data.frame(proportion / rowSums(proportion))","  colnames(proportion) <- c(\"sampleID\",\"cell.type\",\"Freq\")","  proportion <- reshape2::dcast(proportion, sampleID ~ proportion$cell.type, value.var = \"Freq\")","  rownames(proportion) <- proportion$sampleID","  proportion <- proportion[,-1]","  group.predict <- patient_group_predict(proportion)","  return(group.predict)","}"],"stylingDirectives":[[{"start":8,"end":16,"cssClass":"pl-smi"}],[{"start":0,"end":18,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":0,"end":66,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":0,"end":131,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":0,"end":41,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[{"start":0,"end":33,"cssClass":"pl-c"},{"start":0,"end":1,"cssClass":"pl-c"}],[],[{"start":0,"end":9,"cssClass":"pl-en"},{"start":10,"end":12,"cssClass":"pl-k"},{"start":13,"end":21,"cssClass":"pl-k"},{"start":22,"end":28,"cssClass":"pl-smi"},{"start":30,"end":36,"cssClass":"pl-smi"},{"start":37,"end":46,"cssClass":"pl-v"},{"start":47,"end":48,"cssClass":"pl-k"},{"start":49,"end":53,"cssClass":"pl-c1"}],[{"start":2,"end":14,"cssClass":"pl-smi"},{"start":15,"end":17,"cssClass":"pl-k"},{"start":28,"end":37,"cssClass":"pl-smi"},{"start":47,"end":53,"cssClass":"pl-smi"}],[{"start":2,"end":8,"cssClass":"pl-smi"},{"start":9,"end":11,"cssClass":"pl-k"},{"start":28,"end":34,"cssClass":"pl-smi"},{"start":36,"end":48,"cssClass":"pl-smi"},{"start":52,"end":53,"cssClass":"pl-k"},{"start":58,"end":59,"cssClass":"pl-c1"}],[{"start":2,"end":8,"cssClass":"pl-smi"},{"start":9,"end":11,"cssClass":"pl-k"},{"start":12,"end":18,"cssClass":"pl-smi"},{"start":19,"end":31,"cssClass":"pl-smi"}],[{"start":2,"end":7,"cssClass":"pl-smi"},{"start":8,"end":10,"cssClass":"pl-k"},{"start":21,"end":27,"cssClass":"pl-smi"},{"start":29,"end":32,"cssClass":"pl-k"},{"start":43,"end":49,"cssClass":"pl-smi"}],[{"start":2,"end":10,"cssClass":"pl-smi"},{"start":11,"end":13,"cssClass":"pl-k"}],[{"start":2,"end":5,"cssClass":"pl-k"},{"start":6,"end":7,"cssClass":"pl-smi"},{"start":8,"end":10,"cssClass":"pl-k"},{"start":11,"end":12,"cssClass":"pl-c1"},{"start":12,"end":13,"cssClass":"pl-k"},{"start":18,"end":23,"cssClass":"pl-smi"}],[{"start":4,"end":9,"cssClass":"pl-smi"},{"start":10,"e